#include <iostream>
#include <vector>
#include <unordered_map>
#include <algorithm>
#include <queue>
#include <stdlib.h>
#include <limits.h>
#include <fstream>
using namespace std;
typedef long long ll;

int main(){
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr); 
    string words;
    while(cin >> words){
        if(words == "Jeffery"){
            fstream file;
            file.open("./flag.txt", ios::out);
            file << "{_}bonk";
            file.close();
            cout << "flag.txt created\nTo get the flag, just clean up all the trash words.\nps. trash words may appears in any cases(ex. Bonk, BONK ...)";
        }
    }
    return 0;
}


